fn main() {
    assert_eq!(
        13,
        Solution::longest_beautiful_substring("aeiaaioaaaaeiiiiouuuooaauuaeiu".to_string())
    );
    assert_eq!(2169, Solution::longest_beautiful_substring("".to_string()));
}

struct Solution;

use std::cmp::Ordering::{Equal, Greater, Less};
impl Solution {
    pub fn longest_beautiful_substring(word: String) -> i32 {
        let mut ans = 0;
        let mut count = 1;
        let mut len = 1;
        let word = word.as_bytes();
        for (i, b) in word.iter().enumerate().skip(1) {
            match word[i - 1].cmp(b) {
                Less => {
                    count += 1;
                    len += 1;
                }
                Equal => {
                    len += 1;
                }
                Greater => {
                    count = 1;
                    len = 1;
                }
            }
            if count == 5 {
                ans = ans.max(len);
            }
        }
        ans
    }
}
