fn main() {
    assert_eq!(
        13,
        Solution::longest_beautiful_substring("aeiaaioaaaaeiiiiouuuooaauuaeiu".to_string())
    );
    assert_eq!(2169, Solution::longest_beautiful_substring("".to_string()));
}

struct Solution;

impl Solution {
    pub fn longest_beautiful_substring(word: String) -> i32 {
        let mut len = 0;
        let mut ans = 0;
        let mut prev = 0;
        let word = word.as_bytes();
        for &b in word {
            match (prev, b) {
                (0, b'a')
                | (b'a', b'a')
                | (b'a', b'e')
                | (b'e', b'e')
                | (b'e', b'i')
                | (b'i', b'i')
                | (b'i', b'o')
                | (b'o', b'o') => {
                    prev = b;
                    len += 1;
                }
                (b'o', b'u') | (b'u', b'u') => {
                    prev = b;
                    len += 1;
                    ans = ans.max(len);
                }
                (_, b'a') => {
                    prev = b'a';
                    len = 1;
                }
                _ => {
                    prev = 0;
                    len = 0;
                }
            }
        }
        ans
    }
}
